<?php date_default_timezone_set('Asia/Jakarta'); ?>
var serverTime = new Date(<?php print date('Y, m, d, H, i, s, 0'); ?>);
var clientTime = new Date();
var Diff = serverTime.getTime() - clientTime.getTime(); 
function displayServerTime(){
	var clientTime = new Date();
	var time = new Date(clientTime.getTime() + Diff);
	var sh = time.getHours().toString();
	if (sh > 12) {
        sh -= 12;
    } else if (sh === 0) {
        sh = 12;
    }
	var sm = time.getMinutes().toString();
	var ss = time.getSeconds().toString();
	var ampm = time.getHours() >= 12 ? 'PM' : 'AM';
	//document.getElementById("clock").innerHTML = (sh.length==1?"0"+sh:sh) + ":" + (sm.length==1?"0"+sm:sm) + ":" + (ss.length==1?"0"+ss:ss) + " " + ampm;
	document.getElementById("timer").innerHTML = (sh.length==1?"0"+sh:sh) + ":" + (sm.length==1?"0"+sm:sm) + ":" + (ss.length==1?"0"+ss:ss) + " " + ampm;
}
